mapper.select:
select cart_num, img_num, id, cartid, to_char(cdate, 'yyyy-mm-dd') cdate, r
from(
	select cart_num, img_num, id, cartid, cdate, rownum r
	from(
		select cart_num, img_num, id, cartid, cdate
		from cart
		order by cart_num DESC
		)
	)
	where r >= 1 and r <= 1

select cart_num, img_num, id, cartid, to_char(cdate, 'yyyy-mm-dd') cdate, r
from(
	select cart_num, img_num, id, cartid, cdate, rownum r
	from(
		select cart_num, img_num, id, cartid, cdate
		from cart
		order by cart_num DESC
		)
	)
	where id =#{id}
