mapper.select:
select
grade from member
where id=#{id}

mapper.update:
update member
set grade =
#{grade}
where id = #{id}

update member
set password =
#{password}
where id = #{id}

mapper.select:
select
count(*) from member
where id = #{id}
and password = #{password}

